$(function(){var Api,blocks=$(".block");Api={search:function(place,val){blocksFilter=blocks.map(function(i,e){var $e=$(e);var name=$e.find("p."+place).html();if(place==="name"){name=name.toLowerCase()}if(name.indexOf(val)!==-1){return e}});blocks.hide();blocksFilter.show()},showAll:function(){blocks.show()}};blocks.addClass("onload");$("input#search").on("keyup",function(e){var $this=$(this),val=$this.val().toLowerCase();$.trim(val)?Api.search("name",val):Api.showAll()});$("button.btn").on("click",function(e){var $this=$(this),val=$.trim($this.html());$("input#search").val("");$.trim(val)?Api.search("group",val):Api.showAll()})});